mapper.select:
SELECT

FROM tb_class_audit_record
WHERE flow_id = #{flowId} AND audit_role_id = #{auditRoleId}

mapper.update:
UPDATE tb_class_audit_record
SET auditor = #{auditor}, audit_result=#{auditResult}, audit_status=#{auditStatus}, record_id = #{recordId}
WHERE flow_id = #{flowId} AND audit_role_id = #{auditRoleId}
